mapper.insert:
INSERT INTO fmlgEdcFarmmDtl(
	cntntsNo, cntntsSj, locplc, thema, appnYear, 
	url, telno, crtfcYearInfo, cn, imgUrl1, 
	imgUrl2, imgUrl3, imgUrl4, imgUrl5, imgUrl6
) VALUES (
	#{cntntsNo}, #{cntntsSj}, #{locplc}, #{thema}, #{appnYear}, 
	#{url}, #{telno}, #{crtfcYearInfo}, #{cn}, #{imgUrl1}, 
	#{imgUrl2}, #{imgUrl3}, #{imgUrl4}, #{imgUrl5}, #{imgUrl6}
)

mapper.select:
SELECT cntntsNo, cntntsSj, locplc, thema, appnYear, url, telno, crtfcYearInfo, cn, imgUrl1, imgUrl2, imgUrl3, imgUrl4, imgUrl5, imgUrl6 FROM fmlgEdcFarmmDtl WHERE 1=1
AND cntntsNo != #{cntntsNo}
Order by rand() Limit 1
