mapper.insert:
INSERT INTO MEMBER(
	MNO, ID, PASSWORD, 	
	ROLE, NAME, PHONE,
	EMAIL, ADDRESS, IMAGE
) VALUES (
	 0, #{id}, #{password},
	 default, #{name}, #{phone},
	 #{email}, #{address}, #{imageUrl}
)

mapper.update:
	 	UPDATE MEMBER 
	 		SET PASSWORD = #{password} 
 		WHERE MNO = #{mNo}

UPDATE MEMBER 
	SET STATUS = 'N' 
WHERE MNO = #{mNo}
