mapper.select:
		SELECT  
			COUNT(*)
		FROM Product
		WHERE 1=1
		
			AND TITLE LIKE '%${title}%' 
				
		
			AND (
				
					TITLE LIKE '%${item}%'
				
			)
			
		
			AND lprice < #{highPrice}
				
		
			AND lprice > #{lowPrice}
				
		
			AND(
		
        		TITLE LIKE '%${item}%'
    	
    		)

SELECT * FROM Product WHERE 1=1
AND TITLE LIKE '%${title}%'
AND (
TITLE LIKE '%${item}%'
)
AND lprice <= #{highPrice}
AND lprice >= #{lowPrice}
AND(
TITLE LIKE '%${item}%'
)
ORDER BY pNo DESC
ORDER BY lprice ASC
ORDER BY lprice DESC
LIMIT ${limit} OFFSET ${offset}

SELECT * FROM ProductReply 
INNER JOIN Member ON ProductReply.mNo = Member.mNo
WHERE pno = #{pno}

		select * from cart
		where 1=1
		
			and mno = #{mno}
    	
		
			and pno = #{pno}
